[h,if(whichMP == "Add New"),CODE:
{
     [name = "---"]
     [bonus = 0]
     [penalty = 0]
     [trained = 0]
};
{
     [mpDetails = json.get(ArmorOwned, whichMP)]
     [bonus = json.get(mpDetails, "ArmorBonus")]
     [penalty = json.get(mpDetails, "ArmorPenalty")]
     [trained = json.get(mpDetails, "ArmorTrained")]
     [name = whichMP]
}]

[if(whichMP != "Add New" && delete),CODE:
{
     [h: ArmorOwned = json.remove(ArmorOwned, whichMP)]
     [r:whichMP] has been deleted.
     [h: continue=0]

     [h: Armor  = if(whichMP == ArmorType, 0, Armor)]
     [h: ArmorPenalty  = if(whichMP == ArmorType, 0, ArmorPenalty)]
     [h: ArmorType = if(whichMP == ArmorType, "None", ArmorType)]


};
{

